import './App.css';
import Item from './components/Item';
import NewForm from './components/NewForm';
import { useState, useEffect } from 'react';

function App() {
  const [add, showAdd] = useState(false);
  const [selectItem, setSelectItem] = useState(-1);
  const [editItem, setEditItem] = useState(-1);
  const [items, editItems] = useState([
    {
      title: "Something to do",
      completed: false,
      notes: "must be completed before Thursday",
      dueBy: ""
    },
    {
      title: "Other thing to do",
      completed: false,
      notes: "doesn't actually need to be completed",
      dueBy: ""
    }
  ]);

  const addItem = (item) => {
    editItems([...items, item]);
    fetch('/add', {
      body: JSON.stringify({item})
    })
  }
  const changeItem = (index, item) => {
    editItems(items.map((i, ind) => ind === index ? item : i));
    fetch('/edit', {
      body: JSON.stringify({item, index})
    })
  }
  const deleteItem = (index) => {
    editItems(items.filter(i => i !== items[index]));
    fetch('/delete', {
      body: JSON.stringify({index})
    })
  }

  useEffect(() => {
    fetch('/')
    .then(resp => resp.json())
    .then(json => {
      editItems(json.items);
    })
  })


  return (
    <div className="App">
      <span className='title'>Todo</span>
      <div className='content'>
        <div className='content-top'>
          <div style={{textAlign: 'right'}}><button onClick={() => showAdd(!add)} className='new-btn'>New Item</button></div>
          {add && <NewForm addItem={addItem} close={() => showAdd(false)} /> }
        </div>
        {items.map((item, ind) => (
            !item.completed && <Item details={item} ind={ind} select={setSelectItem} edit={setEditItem} clicked={ind === selectItem} editable={ind === editItem} onDelete={deleteItem} editItem={changeItem}  />
          )
        )}

        {
          items.filter(item => item.completed).length > 0 && 
            <div style={{marginTop: '3rem'}}>
              <span className='title'>Completed</span>
              {
                items.map((item, ind) => (
                    item.completed && <Item details={item} ind={ind} select={setSelectItem} edit={setEditItem} clicked={ind === selectItem} editable={ind === editItem} onDelete={deleteItem} editItem={changeItem}  />
                  ) 
                )
              }             
            </div>
        }

      </div>
    </div>
  );
}

export default App;
